$:/plugins/kookma/utility/styles/code.text:
div.kk-utility-code {
	position: relative;
}
div.kk-utility-code > button.tc-btn-invisible {
	position: absolute;
	right: 0;
	padding: 0.1em 0.2em;
	opacity: 0.3;
}
div.kk-utility-code > button.tc-btn-invisible:hover {
	transition: opacity 150ms ease-in-out;
	opacity: 1;
}
div.kk-utility-code > button.tc-btn-invisible svg {
	width: 1.2em;
	height: 1.2em;
}
/* code caption*/
.kk-utility-code-caption{
	background: <<color pre-background>>;
	border: solid 1px <<color pre-border>>;
	border-bottom: unset;
	padding: 0.3em 0.5em;
	border-radius: 3px 3px 0 0;
}
.kk-utility-code-caption:empty{
	display:none;
}
.kk-utility-code-caption + div.kk-utility-code > pre {
	border-radius: 0 0 3px 3px;
	margin-top: 0em;}
/*
Consider changing this rule: It hides the copy-button when
in a tiddler the code-body field is set to yes 
This may causes unwanted results. 	 
*/
div.tc-tiddler-body > div > div > button.tc-btn-invisible{
	display:none;
}

$:/plugins/kookma/utility/styles/control.text:
 \rules only commentblock conditional
<!-- by default controls of view toolbar for shadow tiddlers are hidden -->
<%if [[$:/config/Utility]getindex[toolbar-controls-on-shadows]else[hide]match[hide]] %>

.tc-tiddler-shadow .tc-tiddler-controls
   button:not(.tc-btn-\%24\%3A\%2Fcore\%2Fui\%2FButtons\%2Fclose)
{
	display:none;
}

<%endif%>

$:/plugins/kookma/utility/styles/reveal-.text:
details.kk-utility-details summary {
	opacity:0.2;
	font-size:0.9em;
	display: inline;
	cursor: pointer;
	padding: 10px;
	transition: 0.3s;
	-webkit-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
	user-select: none;
}
details.kk-utility-details summary:hover,
details.kk-utility-details[open] > summary {
	opacity:1;
}

/* hide in preview pane */
.tc-tiddler-preview-preview .kk-utility-details{
	display: none; 
}


/* used in $:/plugins/kookma/utility/ui/ControlPanel/Settings */
.kk-utility-label{
	width:26em;
	display: block;
	margin-top:5px;
}

.kk-ut-txt{
	min-width:50%;
}

$:/plugins/kookma/utility/styles/subtitl.text:
.tc-subtitle svg { /* icons for created/updated on subtitle */
	font-size:1.3em;
}

$:/plugins/kookma/utility/styles/tiddler.text:
/* tiddler size table */

.kk-utility-tiddlersize{
	margin:0.5em;
}
.kk-utility-tiddlersize tr,
.kk-utility-tiddlersize th,
.kk-utility-tiddlersize td {
	padding:0 0.25em;
	margin:0; 
	text-align:right;
}

/* tiddler size button and dropdown in Edit mode */
.kk-utility-tiddlersize-btn{
	float:left;
	margin-top:-1.25em;
}
.kk-utility-tiddlersize-dropdwon{
	padding:0;
	font-size:90%;
	min-width:unset;
}

$:/plugins/kookma/utility/styles/tw-vers.text:
 \rules only commentblock conditional macrocallinline
<%if [[$:/config/Utility]getindex[tw-version]match[show]] %>

/* TW-version added to site title */
.tc-site-title:before {
  content:"TW <<version>>";
  position:absolute;
  margin-top:-1.9em;
  color:<<color sidebar-foreground>>;
  font-size:13px;
}

<%endif%>

$:/plugins/kookma/utility/styles/wikitex.text:
.doc-example {
	margin: 1em 0;
	padding: 0.8em 0;
}
.doc-example:hover {
	background-color: <<colour code-background>>;
}
.doc-example ul {
	margin-bottom: 0;
	padding-bottom: 0;
	margin-top: 0.2em;
}

.doc-example input[type=search] { 
  width: 95%;
}
.doc-example pre:first-child {
	margin-top: 0;
}
