"},"$:/plugins/linonetwo/opened-tiddlers-bar/style.css":{"title":"$:/plugins/linonetwo/opened-tiddlers-bar/style.css","tags":"$:/tags/Stylesheet","text":"\rules except horizrule dash

/**
 * 由于 Tiddlywiki 会把 base.tid 里的 -- 识别为分隔符，变为 - ，所以如果需要用变量，就用 \rules except horizrule dash
 */

/** 顶部标题栏半透明，别的组件都得根据它下移 */
body.tc-body {
  --menubar-height: {{$:/plugins/linonetwo/opened-tiddlers-bar/configs/bar-height}}px;
}

/** make things in $:/tags/AboveStory position fixed, so it will always on the top */
section.story-backdrop .opened-tiddlers-bar-container {
  position: fixed;
  top: 0;
  z-index: 1000;
  height: var(--menubar-height);
  width: 100vw;
  left: 0;

  overflow-x: auto;
  white-space: nowrap;
  overflow: -moz-scrollbars-none;
  scrollbar-width: none;
}
.opened-tiddlers-bar {
  height: var(--menubar-height);
}
section.story-backdrop .opened-tiddlers-bar-container::-webkit-scrollbar {
  display: none;
}

section.story-backdrop div.opened-tiddlers-bar div.opened-tiddlers-bar-tabs {
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
}
/** make it possible to drag the app using this bar in TidGi, when TidGi's titleBar is closed */
<% if [[$:/state/titleBarOpened]get[text]match[no]] %>
div.opened-tiddlers-bar-tabs {
  width: 90vw;
  overflow-x: auto;
  white-space: nowrap;
  overflow: -moz-scrollbars-none;
  scrollbar-width: none;
}
div.opened-tiddlers-bar-tabs::-webkit-scrollbar {
  display: none;
}
div.opened-tiddlers-bar-drag-area {
  width: 5vw;
  -webkit-app-region: drag;
}

/** when hide titlebar and hide sidebar (and on macOS, windows not tested), move bar right to avoid collision with close button on the left top of app */
/** move right a bit to avoid collision with close button on macOS when title bar hide */
<% if [[$:/state/sideBarOpened]get[text]match[no]] %>
section.story-backdrop {
  width: 100vw;
  padding-left: 68px;
}
<% endif %>
<% endif %>

/** tiddlers bar */
section.story-backdrop > p > .opened-tiddlers-bar-container, section.story-backdrop > p > .opened-tiddlers-bar-container > div.tc-tab-buttons {
  width: 100%;
  margin: 0;
  display: flex;
  background: transparent;
}
section.story-backdrop > p > .opened-tiddlers-bar-container {
  background-color: white;
  backdrop-filter: blur(3px);
  background: <<colour tab-background-selected>>;
}


div.opened-tiddlers-bar-tabs button.tc-tab {
  height: calc(100% - 2px);
  border-radius: 0;
  margin: 0;
  padding: 5px 10px;
  padding-right: 2px;
}
/** x button */
div.opened-tiddlers-bar-tabs button.opened-tiddlers-bar-close-button {
  border: none;
  border-radius: 0;
  margin: 0;
  padding: 0 5px;
  opacity: 0;
  background-color: transparent;
}
/** show x when tab is hovered */
div.opened-tiddlers-bar-tabs:hover button.opened-tiddlers-bar-close-button {
  opacity: 1;
}
div.opened-tiddlers-bar-tabs span.tc-eventcatcher {
  display: inline-block;
  height: 100%;
}
section.story-backdrop div.opened-tiddlers-bar-tabs button.tc-tab:nth-child(2) {
  margin-left: -1em;
}
div.opened-tiddlers-bar.tc-tab-buttons {
  padding: 0;
  margin: 0;
}

/** 收起侧边栏的按钮 */
.tc-topbar-right {
  top: 18px;
}
"},"$:/plugins/linonetwo/opened-tiddlers-bar/tree":{"title":"$:/plugins/linonetwo/opened-tiddlers-bar/tree","text":"<<tree prefix:"$:/plugins/linonetwo/opened-tiddlers-bar/">>"},"$:/plugins/linonetwo/opened-tiddlers-bar/ui":{"title":"$:/plugins/linonetwo/opened-tiddlers-bar/ui","tags":"$:/tags/Global","text":"\procedure on-opened-tiddlers-bar-item-middle-click()
<$list filter="[<event-mousebutton>match[middle]]">
<$action-sendmessage $message="tm-close-tiddler" $param=<<currentTab>>/>
</$list>
\end

\procedure opened-tiddlers-bar()
\whitespace trim
<div class="tc-tab-buttons opened-tiddlers-bar" >
  <div class="opened-tiddlers-bar-drag-area" />
  <div class="opened-tiddlers-bar-tabs">
    <$list filter="[list[$:/StoryList]]" history="$:/HistoryList" variable="currentTab">
      <$reveal type="match" state="$:/HistoryList!!current-tiddler" text=<<currentTab>>>
        <$eventcatcher $auxclick=<<on-opened-tiddlers-bar-item-middle-click>>>
          <$button to=<<currentTab>> class="tc-tab-selected tc-tab">
            <$view tiddler=<<currentTab>> field="name">
            <$view tiddler=<<currentTab>> field="title" /></$view>
            <$button message="tm-close-tiddler" param=<<currentTab>> class="tc-image-button opened-tiddlers-bar-close-button">
              &times;
            </$button>
          </$button>
        </$eventcatcher>
      </$reveal>
      <$reveal type="nomatch" state="$:/HistoryList!!current-tiddler" text=<<currentTab>>>
        <$eventcatcher $auxclick=<<on-opened-tiddlers-bar-item-middle-click>>>
          <$button to=<<currentTab>> class="tc-tab" >
            <$view tiddler=<<currentTab>> field="name">
              <$view tiddler=<<currentTab>> field="title" />
            </$view>
            <$button message="tm-close-tiddler" param=<<currentTab>> class="tc-image-button opened-tiddlers-bar-close-button">
              &times;
            </$button>
          </$button>
        </$eventcatcher>
      </$reveal>
    </$list>
  </div>
  <div class="opened-tiddlers-bar-drag-area" />
</div>
\end
"}}}
